(function(){app.filter("appDate",function(){return function(n){return moment(n).isValid()?moment.locale()==="zh-cn"?moment(n).format("YYYY-M-D"):moment(n).format("l"):""}});app.filter("appDateTime",function(){return function(n){return moment(n).isValid()?moment.locale()==="zh-cn"?moment(n).format("YYYY-M-D HH:mm:ss"):moment(n).format("lll"):""}});app.filter("appFullDateTime",function(){return function(n){return moment(n).isValid()?(moment.locale()==="zh-cn",moment(n).format("llll")):""}})}).call(this);